mapper.select:
SELECT writer
FROM question
WHERE qno = ${qno}

SELECT qno, category, subject, writer, regDate
FROM question 


	AND category IN (

			#{item}

	)

		AND subject LIKE '%${keyword}%' OR content LIKE '%${keyword}%' 

		AND writer = #{writer}

SELECT qno, category, subject, content, writer, regDate, isSecret
FROM question
WHERE qno = ${qno}

mapper.update:
update question
category = #{category}, subject = #{subject}, content = #{content}, isSecret = #{isSecret},
where qno = ${qno}
