mapper.select:
select IFNULL(score, 0)
from apt_score
where memberId = #{memberId} and aptCode = #{aptCode}

select memberId, aptCode, aptName, score
from apt_score
where memberId = #{memberId}

select AVG(score) as average, aptName, aptCode 
from apt_score
where aptCode = #{aptCode}

mapper.update:
update apt_score
set score = ${score}
where memberId = #{memberId} and aptCode = #{aptCode}
