mapper.select:
select count(memberId)
from member
where memberId = #{memberId}

select memberName, memberId, memberPw, memberEmail,memberTel, isManager
from member
where memberId = #{memberId} and memberEmail = #{memberEmail}

select memberName, memberId, memberPw, memberEmail,memberTel, isManager
from member
where memberId = #{memberId}

select memberName, memberId, memberPw, memberEmail,memberTel, isManager
from member
where memberId = #{memberId} and memberPw = #{memberPw}

mapper.update:
update member
memberPw = #{memberPw}, memberEmail = #{memberEmail}, memberName = #{memberName}, memberTel = #{memberTel}, isManager = #{isManager},
where memberId = #{memberId}
